import React from 'react'
import { useState } from 'react'

const Signin = () => {
    const[authValues, setAuthValues] = useState({
        email: '',
        password: ''
    });

    const handleAuthChange = e => {
        setAuthValues({ ...authValues, [e.target.name]: e.target.authValues })
    }



    return (
        <div className="auth__container-right">
            <form action="" className="auth__form">
                <div className="auth__inputs">
                    <label htmlFor="email" className="auth__inputs--label">
                        Email
                    </label>
                    <input 
                        id='email' 
                        type="text" 
                        name="email" 
                        className="form__inputs--input" 
                        placeholder="Enter your email"
                        value= {authValues.email}
                        onChange={handleAuthChange}
                        // value={values.email}
                        // onChange={handleChange}
                    />
                    {/* {errors.email && <p>{errors.email}</p>} */}
                    console.log(e)
                </div>
                <div className="auth__inputs">
                    <label htmlFor="password" className="auth__inputs--label">
                        Password
                    </label>
                    <input 
                        id='password' 
                        type="password" 
                        name="password" 
                        className="auth__inputs--input" 
                        placeholder="Enter your password"
                        value={authValues.password}
                        // onChange={handleAuthChange}
                        // value={values.email}
                        // onChange={handleChange}
                    />
                    {/* {errors.email && <p>{errors.email}</p>} */}
                </div>


                <button className="auth__login-btn" type='submit'>Log In</button>
                <a className="auth__forgotten" href='#'>Forgotten password?</a>
                <button className="auth__signup-btn" type='submit'>Create New Account</button>


            </form>

        </div>
    )
}

export default Signin
